mapper.select:
select *
from deal_info
where product_id = #{productId}
  AND end_date > #{currentTime}
  AND start_date < #{currentTime}

mapper.update:
update deal
set participant_count=#{count}
where id = #{id}

update deal
set status=#{afterStatus}
where status = #{beforeStatus}
  AND end_date > #{time}
  AND start_date < #{time}
